import * as React from 'react';
import './index.scss';
import {useState} from "react";
import ReCAPTCHA from "react-google-recaptcha";
import {commonValidate, validateMail} from "./Service";

const Register: React.FC<any> = () => {

    const [name, setName] = useState<string | null>(null);
    const [mail, setMail] = useState<string | null>(null);
    const [address, setAddress] = useState<string | null>(null);
    const [phoneNumber, setPhoneNumber] = useState<string | null>(null);
    const [pass, setPass] = useState<string | null>(null);
    const [repeatPass, setRepeatPass] = useState<string | null>(null);

    const styleValidate = (check: boolean | null, type: 'text' | 'icon'): React.CSSProperties => {
        if (check) return {}; else if (type === 'text') return {visibility: 'visible'}; else return {color: 'red'};
    }

    console.log(styleValidate((commonValidate(name, /^[a-zA-z\s]{6,25}$/) || name === null), 'text'))


    const formRegisterLeft = () => {
        return (
            <div className={'form-register-left'}>
                <div className={'form-title'}>Sign up</div>
                <div className={'wrapper-input'}>

                    <div>
                        <div className={'wrapper-item'}>
                            <i className="fas fa-user" style={styleValidate((commonValidate(name, /^[a-zA-z\s]{6,25}$/) || name === null), 'icon')}/>
                            <input type="text" placeholder={'Your Name'} onChange={(event) => setName(event.target.value)}/>
                        </div>
                        <small
                            style={styleValidate((commonValidate(name, /^[a-zA-z\s]{6,25}$/) || name === null), 'text')}
                        >
                            Your name must has 6-50 characters
                        </small>
                    </div>

                    <div className={'wrapper-item'}>
                        <i className="fas fa-envelope" style={(validateMail(mail) || mail === null) ? {} : {color: 'red'}}/>
                        <input type="text" placeholder={'Your Email'} onChange={(event) => setMail(event.target.value)}/>
                    </div>
                    <small style={(validateMail(mail) || mail === null) ? {} : {visibility: 'visible'}}>illegal</small>

                    <div className={'wrapper-item'}>
                        <i className="fas fa-map-marker-alt"
                           style={styleValidate(commonValidate(address, /^[a-zA-z\d\s]{6,50}$/) || address === null, 'icon')}/>
                        <input type="text" placeholder={'Your Address'} onChange={(event) => setAddress(event.target.value)}/>
                    </div>
                    <small style={styleValidate(commonValidate(address, /^[a-zA-z\d\s]{6,50}$/) || address === null, 'text')}>Your name must has 6-50
                        characters</small>

                    <div className={'wrapper-item'}>
                        <i className="fas fa-mobile-alt" style={styleValidate(commonValidate(phoneNumber, /^[\d]{10,10}$/) || phoneNumber === null, 'icon')}/>
                        <input type="text" placeholder={'Phone Number'} onChange={(event) => setPhoneNumber(event.target.value)}/>
                    </div>
                    <small style={styleValidate(commonValidate(phoneNumber, /^[\d]{10,10}$/) || phoneNumber === null, 'text')}>Your name must has 6-50
                        characters</small>

                    <div className={'wrapper-item'}>
                        <i className="fas fa-lock"
                           style={styleValidate(commonValidate(pass, /^[\w\W]{6,}$/) || pass === null, 'icon')}/>
                        <input type="text" placeholder={'Password'} onChange={(event) => setPass(event.target.value)}/>
                    </div>
                    <small style={styleValidate(commonValidate(pass, /^[\w\W]{6,}$/) || pass === null, 'text')}>Your name must has 6-50
                        characters</small>

                    <div className={'wrapper-item'}>
                        <i className="fas fa-unlock"/>
                        <input type="text" placeholder={'Repeat your password'} onChange={(event) => setRepeatPass(event.target.value)}/>
                    </div>
                    <small>Your name must has 6-50 characters</small>

                    <div style={{marginTop: 15}}>
                        <ReCAPTCHA sitekey="6Ldb6foUAAAAANXRBMbMqlZowbdhvcg1NBJGx7OG" onChange={() => {
                        }}/>
                    </div>
                    <button disabled>Register</button>
                </div>
            </div>
        )
    }

    const formRegisterRight = () => {
        return (
            <div className={'form-register-right'}>
                <div className={'wrapper-img'}>
                    <img src='https://colorlib.com/etc/regform/colorlib-regform-7/images/signup-image.jpg' alt='img'/>
                </div>
                <div className={'login-now'} style={{display: "flex", alignItems: "center"}}>
                    <span style={{marginRight: 5}}>Login now</span>
                    <i className="fas fa-long-arrow-alt-right"/>
                </div>
            </div>
        )
    }
    return (
        <div className={'register'}>
            <div className={'wrapper-form'}>
                {formRegisterLeft()}
                {formRegisterRight()}
            </div>
        </div>
    )
}

export default Register;
