import * as React from 'react';
import {Card, Page} from '@shopify/polaris';
import {useEffect, useState} from "react";
import {getListUser, IResListUser} from "../../api/User";
import './index.scss';

const ListUser: React.FC<any> = () => {

    const [listUser, setListUser] = useState<IResListUser[]>([]);
    const [page, setPage] = useState<number>(0);

    const getUsers = async (page: number) => {
        try {
            const res = await getListUser(`https://fakestoreapi.com/users?page=${page}&limit=5`);
            if (res.status === 200) {
                setListUser(res.body);
            }
        } catch (e) {
            console.error(e);
        }
    }

    const cardItem = (item: IResListUser) => {
        return (
            <div key={item.id} style={{marginBottom: 10}}>
                <Card sectioned title={item.username} actions={[{content: 'Check google map'}]}>
                    <div style={{
                        display: "flex"
                    }}>
                        <div style={{
                            height: 75,
                            width: 75,
                            borderRadius: '50%',
                            display: 'flex',
                            justifyContent: 'center',
                            alignItems: 'center',
                            backgroundColor: '#0093E9',
                            backgroundImage: 'linear-gradient(160deg, #0093E9 0%, #80D0C7 100%)',
                            color: 'white',
                            fontWeight: 'bold',
                            fontSize: 24
                        }}>
                            <div>{item.name.firstname[0].toUpperCase()}</div>
                        </div>
                        <div style={{marginLeft: 8}}>
                            <div style={{fontWeight: "bold", textTransform: 'capitalize'}}>{item.name.firstname} {item.name.lastname}</div>
                            <div>{item.address.city}, {item.address.number} {item.address.street}</div>
                            <div>{item.phone}</div>
                        </div>
                    </div>
                </Card>
            </div>
        )
    }

    useEffect(() => {
        getUsers(page)
    }, []);

    return (
        <Page
            narrowWidth
            breadcrumbs={[{content: 'Orders', url: '/register'}]}
            title="List user"
            pagination={{
                onPrevious() {
                    if (page > 0) setPage(page - 1)
                    getUsers(page - 1)
                },
                hasPrevious: (page === 0) ? false : true,
                onNext() {
                    setPage(page + 1)
                    getUsers(page + 1)
                },
                hasNext: true,
            }}
        >
            {
                listUser.map((value) => cardItem(value))
            }
        </Page>
    )
}

export default ListUser;
