import {makeObservable, observable, action} from "mobx";

class BaseService {
    type: 'info' | 'warring' | 'error' | 'success';
    mes: string;

    constructor(type: 'info' | 'warring' | 'error' | 'success', mes: string) {
        makeObservable(this, {
            type: observable,
            mes: observable,
            pushNotify: action,
        })
        this.type = type;
        this.mes = mes;
    }

    pushNotify(type: 'info' | 'warring' | 'error' | 'success', mes: string) {
        this.type = type;
        this.mes = mes;
    }


}

export default new BaseService('info', '');
