import * as React from 'react';
import {Card, Page} from '@shopify/polaris';
import {useEffect, useState} from "react";
import {getListUser, IResListUser} from "../../api/User";
import './index.scss';

const ListUser: React.FC<any> = () => {

    const [listUser, setListUser] = useState<IResListUser[]>([]);

    const getUsers = async () => {
        try {
            const res = await getListUser('https://fakestoreapi.com/users?page=0&limit=5');
            if (res.status === 200) {
                setListUser(res.body);
            }
        } catch (e) {
            console.error(e);
        }
    }

    const cardItem = (item: IResListUser) => {
        return (
            <div key={item.id}>
                <Card sectioned title={item.username} actions={[{content: 'Add variant'}]}>
                    <div>
                        <div style={{
                            height: 75,
                            width: 75,
                            borderRadius: '50%',
                            display: 'flex',
                            justifyContent: 'center',
                            alignItems: 'center',
                            backgroundColor: '#0093E9',
                            backgroundImage: 'linear-gradient(160deg, #0093E9 0%, #80D0C7 100%)',
                            color: 'white',
                            fontWeight: 'bold',
                            fontSize: 24
                        }}>
                            <div>{item.name.firstname[0].toUpperCase()}</div>
                        </div>
                        <div>
                            <div>{item.name.firstname} {item.name.lastname}</div>
                            <div>{item.address}</div>
                        </div>
                    </div>
                </Card>
            </div>
        )
    }

    useEffect(() => {
        getUsers()
    }, []);

    return (
        <Page
            narrowWidth
            breadcrumbs={[{content: 'Orders', url: '/register'}]}
            title="Add payment method"
            pagination={{
                hasPrevious: false,
                hasNext: true,
            }}
        >
            {
                listUser.map((value) => cardItem(value))
            }
        </Page>
    )
}

export default ListUser;
